$(function() { 

 // create placeholder data attributes so the original placeholder is stored
  $('form[data-validate="true"] input, form[data-validate="true"] textarea').each(function(){
    var currentField = $(this);
    var placeholderValue = currentField.attr('placeholder');

    currentField.attr('data-placeholder', placeholderValue);
  });

  var formValid = false;

  function checkAllValid(parentForm){
    var selectWrap = parentForm.find('.is-requiredSelect');
    var numSelects = selectWrap.length;
    var formGroup = parentForm.find('.formGroup.is-required');
    var groupChecked = parentForm.find('.formGroup.is-required :checkbox:checked');
    var numGroups = formGroup.length;
    var checkValidated = true;
    var inputsValid = true;
    var selectsValid = true;
    var submit = parentForm.find('input[type="submit"]');


    //check for select menus
    if(numSelects > 0) {
      selectWrap.each(function(){
        var thisSelect = $(this);
        var selected = thisSelect.find("option:selected");

        if(selected.hasClass('placeholder')){
          selectsValid = false;
        }
      });
    }

    //check for checkbox groups
    if(numGroups > 0) {
      formGroup.each(function(i){
        var thisGroup = $(this);
        var isChecked = thisGroup.find(":checkbox").is(':checked');
        
        if(!isChecked) {
          checkValidated = false; 
        } 
      });
    }

    //Check for text inputs and textareas
    parentForm.find('input, textarea').each(function(){
      var isValid = $(this)[0].checkValidity();

      if (!isValid){
        inputsValid = false;
      }
    });

    if (checkValidated && inputsValid && selectsValid) {
      formValid = true;
    } else {
      formValid = false;
    }

    // the disabled button boolean will be the opposite state of formValid - so if all fields are falid, the disabled state is false (turned on)
    submit.prop('disabled', !formValid);
  }


  // Form validation
  $('form[data-validate="true"] input, form[data-validate="true"] textarea, form[data-validate="true"] select').on('keyup change', function (e) {
    
    var currentField = $(this);
    var parentForm = currentField.closest('form[data-validate="true"]');
    var placeholderValue = currentField.attr('placeholder');
    var inputWrap = currentField.closest('.FloatLabel');
    var currentValid = currentField[0].checkValidity();
    var originalPlaceholder = currentField.data('placeholder');

    
    if(currentValid) {

      //if a field is not required and doesnt have a value it should not show check mark
      if (currentField.val() !== '') {
        inputWrap.addClass("validatePass");
        inputWrap.removeClass("validateFail");
        inputWrap.closest('.FloatLabel').removeClass('is-required');

        //reset label back to correct state
        currentField.next(".FloatLabel-label").text(originalPlaceholder);

      } else {
        inputWrap.removeClass("validatePass");
      }

      //if value is blank, dont add this
    } else {
      inputWrap.removeClass("validatePass");

      //if field is blank and there is an error, dont show the error state
      if (currentField.val() === '') {
        
        inputWrap.closest('.FloatLabel').addClass('is-required');

        if (!inputWrap.hasClass("validateFail")) {
          currentField.next('label').removeClass('is-visible');
          currentField.removeClass('is-active');
        } else {
          currentField.next('label').addClass('is-visible');
          currentField.addClass('is-active');
        }
      } else {
        inputWrap.closest('.FloatLabel').addClass('is-required');
      }
    }

    checkAllValid(parentForm);
  });

  $('form[data-validate="true"] select').on('change', function () {
    var thisSelect = $(this);
    var selected = thisSelect.find("option:selected");

    //if there is a selected option
    if(selected.length) {
      thisSelect.next('.chosen-container').find('.chosen-single span').addClass('validatePass');
    }
  });


    

  //we dont want to bother the user with an error state before they switch fields, so we want error handeling to happen on change
  $('form[data-validate="true"] input, form[data-validate="true"] textarea').on('blur', function (e) {
    var currentField = $(this);
    var parentForm = currentField.closest('form[data-validate="true"]');
    var placeholderValue = currentField.attr('placeholder');
    var fieldTitle = currentField.attr('title');
    var errorMsg = fieldTitle;
    var inputWrap = currentField.closest('.FloatLabel');
    var currentValid = currentField[0].checkValidity();
    var submit = parentForm.find('input[type="submit"]');
    var originalPlaceholder = currentField.data('placeholder');


    if (!currentValid) {
      //add error icon
      inputWrap.addClass("validateFail");

      //if no title exists on input, we have a fallback error message
      if (fieldTitle === undefined) {
        errorMsg = 'cannot be blank';
      }
      currentField.next('label').addClass('is-visible').text(originalPlaceholder + ' ' + errorMsg);
      currentField.addClass('is-active');

    } else {
      inputWrap.removeClass("validateFail");
      currentField.attr("placeholder", originalPlaceholder);
    }
  });
});


// on load, after floatlabels is loaded, we add required classes to the floatlabel wrapper
$(window).load(function() {
  $('form[data-validate="true"] input, form[data-validate="true"] textarea').each(function(){
    var currentField = $(this);

    if(currentField.prop('required')){
      currentField.closest('.FloatLabel').addClass('is-required');
    }
  });
});
$(function() {


  $(document.body).on('click', 'a', function(event) {
    // Prevent the page scrolling to top when clicking example buttons
    if (event.target.getAttribute('href') === '#') {
      event.preventDefault();
    }
  });


  // Automatically track click on elements with data-track values
  $('[data-track]').on('click', function() {
    var trackedEvent = $(this).attr('data-track') ? $(this).attr('data-track') : "";
    if (trackedEvent.length > 0 && window.analytics) {
      window.analytics.track(trackedEvent);
    }
  });

  //helper function to ensure that given $content is visible on screen
  var CONTENT_PADDING = 5;
  function scrollToContent($content) {
    if($content.length) {
      var dimensions = $content[0].getBoundingClientRect();
      //if the content fits on the screen
      if(dimensions.height + CONTENT_PADDING < window.innerHeight) {
        //scroll down if the bottom of the content is hidden
        var delta = Math.max(0, dimensions.bottom + CONTENT_PADDING - window.innerHeight);
        if(delta) {
          window.scrollBy(0, delta);
        }
      }
    }
  }

  $(".chosen").chosen({
    width: "100%",
    disable_search_threshold: 5
  }).on('chosen:showing_dropdown', function (e, $drop) {
    // chosen content takes time to render
    // and this is the only event we can subscribe to :(
    window.setTimeout(function () {
      scrollToContent($drop.chosen.dropdown);
    }, 300);
  });

  // when clicking into a validated field, remove validation msg
  $('.is-required').on('keyup', function() {
    var validationMsg = $(this).closest('.columns').find(".Input-message");
    validationMsg.addClass('Input-message--thanks').text('Thanks :]')
    $(this).removeClass('is-required');
  });

  // when clicking on validation copy, focus the input (which removes messaging)
  $(".Input-message").on("click", function(){
    var validationInput = $(this).parent().find("input");
    validationInput.focus();
  });

  $(".Ticket").on('click', function(){
    var $this = $(this);
    if($this.hasClass('is-collapsed')) {
      $this.removeClass('is-collapsed');
    } else {
      $this.addClass('is-collapsed')
    }
  });
  //reavealer - TODO add hash support to open revealer if hash matches existing tab
  $('body').on("click", "*[data-trigger='revealer']", function(){
    var $this = $(this);

    if($this.attr('data-revealerid')) {
      var curId = $this.attr('data-revealerid');
      var shelf = $('body').find('*[data-shelfid="' + curId + '"]');
      var shelfHeight = shelf.find('.shelf-inner').outerHeight(true);
    } else {
      var shelf = $this.next('.Revealer-shelf');
      var shelfHeight = shelf.find('.shelf-inner').outerHeight(true);
    }
    if(shelf.find('.shelf-inner').length != 0){
      $this.closest('.Revealer-header').toggleClass('is-open');
      shelf.toggleClass('is-open');
    }else {
      $this.closest('.Revealer-header').toggleClass('is-open legacy').next('.Revealer-shelf').toggleClass('is-open legacy');
    }
    if(shelf.hasClass('is-open')) {
      shelf.css('height', shelfHeight);
    } else {
      shelf.css('height', 0);
    }
  });

  //bootstrap dropdown stuff
  $('.Dropdown').on('show.bs.dropdown', function () {
    var Button = $(this).find('.Button');
    if(!Button.hasClass('Button--icon')) {
      Button.addClass('is-flipped');
    }
  });

  $('.Dropdown').on('shown.bs.dropdown', function () {
    scrollToContent($(this).find('.Dropdown-content'));
  });

  $('.Dropdown').on('hide.bs.dropdown', function () {
    var Button = $(this).find('.Button');
    if(!Button.hasClass('Button--icon')) {
      Button.removeClass('is-flipped');
    }
  });

  $('.Dropdown').on('click.bs.dropdown', function(e) {
    var $clicked = $(e.target);
    //if they click within dropdown body
    if($clicked.closest('.Dropdown-content').length) {
      var actionSelector = '[data-dropdown-action], a, button, input[type="submit"]';
      //if its an actionable item, close the dropdown unless the action contains data-dropdown-leave-open
      if($clicked.closest(actionSelector).length && !$clicked.closest('[data-dropdown-leave-open]').length) {
        $(this).removeClass('open');
      } else {
        //clicked on the dropdown body, leave it open
        e.stopPropagation();
      }
    }
  });

  $('ul').find('[data-toggle="tab"]').first().closest('li').removeClass('is-active');

  //bootstrap tooltips
  $('[data-toggle="tooltip"]').each(function () {
    var $this = $(this);
    $this.tooltip({
      container: $this.attr('data-container') || 'body'
    });
  });

  $('[data-toggle="tab"]').on('show.bs.tab', function (e) {
    var $this = $(this);
    var pageName = $this.text().trim();

    $(e.target.hash).find('[data-toggle="tab"]').first().tab('show');

    if ($this.attr('data-page') && window.analytics && pageName) {
      window.analytics.page(pageName);
    }
  });

  //highlight.js
  //hljs.initHighlightingOnLoad();

  $('pre code').each(function(i, block) {
    hljs.highlightBlock(block);
  });

  //Tables
  $.extend( $.fn.dataTable.defaults, {
    paging: false,
    info: false,
    language: {
      search: "_INPUT_",
      searchPlaceholder: "Search"
    }
  } );
  $('.table--sort').DataTable({
  });

  //alerts
  $('.Alert-close').on('click', function(){
    $(this).parent().parent('.Alert').slideUp(300);
  });
});
 $(function() {
  var pane = $('.Tabs-pane');
  if(!pane.length) {
    return;
  }

  var tabs = $('[data-toggle="tab"]');
  var defaultTab = tabs.filter('[data-default]').first();
  var firstTab = tabs.first();
  var win = $(window);
  var hash = window.location.hash;

  function emitTabChangeEvent(tab) {
    var href = tab.attr('href') || '';
    //let bootstrap show the tab, then emit a tab change event
    window.setTimeout(win.trigger.bind(win, 'tab-change', href.replace(/^.*#/, '')), 0);
  }

  function getLinkFromHash(hash) {
    return hash.replace(/^#tab-/, '#actions-');
  }

  if(!defaultTab.length) {
    defaultTab = firstTab;
  }
  // XXX(nan) no idea why this is needed, but without it Revealer's are broken and hidden
  firstTab.tab('show');

  tabs.on('show.bs.tab', function (e) {
    emitTabChangeEvent($(this));
  });

  tabs.on('click', function(e) {
    var $this = $(this);

    if(!$this.closest('.Tabs-pane').length > 0) {
      history.pushState( null, null, $this.attr('href') );
    }
    e.preventDefault();
  });

  window.addEventListener('hashchange', function(e) {
    var hash = window.location.hash;
    if (hash.length) {
      tabs.filter('[href=' + getLinkFromHash(hash) + ']').tab('show');
    } else {
      defaultTab.tab('show');
    }
  }, false);

  var initialTab = defaultTab;
  if (hash.length) {
    hash = getLinkFromHash(hash);
    initialTab = tabs.filter(function(i, tab) {
      return tab.href.indexOf(hash) === tab.href.length - hash.length;
    });
    if(pane.closest('.collapse').length){
      $('.collapse').addClass('in').css('height','auto');
    }
  }

  initialTab.tab('show');
  emitTabChangeEvent(initialTab);
});
